mapper.select:
SELECT id, title, content, author, view_count AS viewCount, created_at AS createdAt, updated_at AS updatedAt FROM posts
(title ILIKE '%' || #{q} || '%' OR content ILIKE '%' || #{q} || '%' OR author ILIKE '%' || #{q} || '%')
ORDER BY id DESC LIMIT #{size} OFFSET #{offset}

mapper.delete:
DELETE FROM posts
WHERE id IN

    #{id}
